import React from "react";
import patientIcon from './images/patientIcon.png'

const Patient = (props) => {
  const { firstName, lastName, gender, email, cell } = props.patient;
  return (
    <div className="item" style = { {   
      borderRadius: "15px",
      border: '2px solid #000000',
      padding: '9pt',
      marginTop: '10pt',
      display: 'flex-end',
      alignItems: 'stretch'
    }}>
      <div>
      <img className="ui avatar image" src={patientIcon} alt="user" style = { {   
        width: "45px",
        display: "inline-block",
        padding: "1pt",
        position: "absolute"
      }} />
      <div className="content" style = {{
        display: "inline-block",
        marginLeft: "50px"
      }}>
        <div className="header">{firstName} {lastName}</div>
        <div>{email}</div>
      </div>
      </div>
      <div>
      <button 
            style = {{
                position: 'absolute',
                borderRadius: '10px',
                color: 'black',
                fontSize: '12pt',
                borderColor: 'black',
                height: '45px',
                width: '175px',
                marginLeft: '10px',
                display: "inline-block",
                background: "transparent"
             }} 
            > <b>New Perscription</b>
      </button> 
      </div>
      <div>
      <button 
            style = {{
                position:'fixed',
                borderRadius: '10px',
                color: 'Red',
                fontSize: '12pt',
                borderColor: 'Red',
                height: '45px',
                width: '125px',
                marginLeft: "15%",
                display: "inline-block",
             }} 
            > <b>Delete</b>
        </button>  
        </div>
    </div>
  );
};

export default Patient;
