import {Modal, Button} from "react-bootstrap"
import React, {useState, Component } from 'react';
import { render } from "@testing-library/react";
import Form from 'react-bootstrap/Form';
import PatientSignup from "./patientsignup";

function Perscription() {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
    <button className="perscription" onClick={handleShow}
            style = {{
                position: 'absolute',
                borderRadius: '10px',
                color: 'black',
                fontSize: '12pt',
                borderColor: 'black',
                height: '45px',
                width: '150px',
                marginLeft: "35%",
                display: "inline-block"
             }} 
            > <b>New Perscription</b>
      </button>
      
      <Modal show={show} size="md" onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title
          
          style = {{
            fontWeight: "bold",         
          }}>Make Perscription</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <button className="perscription"
                style = {{
                    position: 'absolute',
                    borderRadius: '10px',
                    color: 'black',
                    fontSize: '12pt',
                    borderColor: 'black',
                    height: '45px',
                    width: '450px',
                    display: "inline-block"
                }} 
                > <b>Choose Perscription</b>
        </button>
        </Modal.Body>
        <Modal.Footer>
            <PatientSignup></PatientSignup>
        
            <button onClick={handleClose}
                class="btn btn-outline-primary"
                style = {{
                    marginRight:'10px',
                    color: 'black',
                    fontSize: '12pt',
                    borderColor: 'white',
                    borderRadius: '7px'
                 }} 
                href="#"> <b>Close</b>
            </button>  
        </Modal.Footer>
      </Modal>

    </>
    );
}
export default Perscription
