import {Modal, Button} from "react-bootstrap"
import React, {useState, Component } from 'react';
import { render } from "@testing-library/react";
import Form from 'react-bootstrap/Form';
import PatientSignup from "./patientsignup";

function Perscription() {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>
    <button className="perscription"
            style = {{
                position: 'absolute',
                borderRadius: '10px',
                color: 'black',
                fontSize: '12pt',
                borderColor: 'black',
                height: '45px',
                width: '150px',
                marginLeft: "35%",
                display: "inline-block"
             }} 
            > <b>New Perscription</b>
      </button>
      
      <Modal show={show} size="md" onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title
          
          style = {{
            fontWeight: "bold",         
          }}>Patient Sign In</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
              <Form.Label style={{
                  color: "black"

                }}
              >Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="name@example.com"
                autoFocus
              />
            </Form.Group>
            <Form.Group
              className="mb-3"
              controlId="exampleForm.ControlTextarea1"
            >
              <Form.Label style={{color: "black"}}
              >Password</Form.Label>
              <Form.Control 
                type="password"
                placeholder="*********"
                autoFocus
              />
            </Form.Group>
          </Form>

        <p>
            Don't have an account? Click the blue Sign Up Button. 
        </p>

        </Modal.Body>
        <Modal.Footer>
            <PatientSignup></PatientSignup>
        
            <button onClick={handleClose}
                class="btn btn-outline-primary"
                style = {{
                    marginRight:'10px',
                    color: 'black',
                    fontSize: '12pt',
                    borderColor: 'white',
                    borderRadius: '7px'
                 }} 
                href="#"> <b>Forgot Password</b>
            </button>  

           <button onClick={handleClose}
            class="btn btn-outline-primary"
            style = {{
                marginRight:'10px',
                color: 'black',
                fontSize: '12pt',
                borderColor: 'black',
                borderRadius: '7px'
             }} 
            href="#"> <b>Login</b>
        </button>  
    

        </Modal.Footer>
      </Modal>

    </>
    );
}
export default Perscription
