import React from "react";

class AddPatient extends React.Component {
  state = {
    firstName: "",
    lastName: "",
    gender: "",
    email: "",
    cell: "",
  };

  add = (e) => {
    e.preventDefault();
    if (this.state.firstName === "" || this.state.lastName === "" || this.state.email === "" || this.state.gender === "" || this.state.cell === "") {
      alert("ALl the fields are mandatory!");
      return;
    }
    this.props.addPatientHandler(this.state);
    this.setState({ firstName: "", lastName: "", gender: "", email: "", cell: ""});
  };

  render() {
    return (
      <div className="patientbox">
        <h2>Add Patient</h2>
        <form className="ui form" onSubmit={this.add}>
          <div className="field">
            <label>First Name</label>
            <input
              type="text"
              name="First Name"
              placeholder="john"
              value={this.state.firstName}
              style = {
                {
                  borderRadius: '25px',
                  border: '2px solid #000000',
                }
              }
              onChange={(e) => this.setState({ firstName: e.target.value })}
            />
          </div>
          <div className="field">
            <label>Last Name</label>
            <input
              type="text"
              name="Last Name"
              placeholder="Last Name"
              value={this.state.lastName}
              onChange={(e) => this.setState({ lastName: e.target.value })}
            />
          </div>
          <div className="field">
            <label>Gender</label>
            <input
              type="text"
              name="Gender"
              placeholder="Gender"
              value={this.state.gender}
              onChange={(e) => this.setState({ gender: e.target.value })}
            />
          </div>
          <div className="field">
            <label>Email</label>
            <input
              type="text"
              name="email"
              placeholder="Email"
              value={this.state.email}
              onChange={(e) => this.setState({ email: e.target.value })}
            />
          </div>
          <div className="field">
            <label>Cell Phone</label>
            <input
              type="text"
              name="Cell Phone"
              placeholder="Cell Phone"
              value={this.state.cell}
              onChange={(e) => this.setState({ cell: e.target.value })}
            />
          </div>
          <button className="ui button blue">Add</button>
        </form>
      </div>
    );
  }
}

export default AddPatient;
