import React, {useState, Component } from "react";
import patientIcon from './images/patientIcon.png';
import Perscription from './startPerscription';
import {Modal, Button} from "react-bootstrap"
import cert from './images/cert.png'

function Med (props) {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const medications = ["DICLOGENAC", "DMSO", "TETRACAINE", "GABAPENTIN", "LIDOCAINE", "BACLOFEN"]
    
  return (
    <div style={{marginBottom: "20px", borderColor: "gray"}}>
        <button onClick={handleShow}
            className="btn btn-outline-success"
            style = {{
                position:'absolute',
                padding: '4px',
                paddingLeft: 'auto',
                borderStyle: "solid",
                paddingRight: 'auto',
                borderRadius: '10px',
                color: 'black',
                fontSize: '12pt',
                borderColor: 'black',
                height: '45px',
                width: '80%',
                display: "inline-block",
                marginBottom: "125px"
            }} 
                > <b>{props.formula}</b>
        </button> 

        <Modal show={show} size="lg" onHide={handleClose}>
        <Modal.Header closeButton> </Modal.Header>
        
        <Modal.Body>
            <img
            src={cert} alt="user" style = { { marginLeft: 'auto', marginRight: 'auto',   
            width: "135px",
            }} /> 
            <h1 style = {{textAlign: "center"}}>Edit Formula</h1>
            <h4 style = {{textAlign: "center"}}>Click one of the boxes below to select the formula</h4>
            <section style={{borderStyle: "dashed", borderColor: "black"}}>
                <br></br>
                
                <h5 style ={{textAlign: "center"}}><b>Topical Ointment: </b>{props.formula}</h5>


            </section>
        </Modal.Body>
        <Modal.Footer>        
            <button onClick={handleClose}
                class="btn btn-outline-primary"
                style = {{
                    marginRight:'10px',
                    color: 'black',
                    fontSize: '12pt',
                    borderColor: 'white',
                    borderRadius: '7px'
                 }} 
                href="#"> <b>Close</b>
            </button>  
        </Modal.Footer>
      </Modal>


    </div> 
  );
};

export default Med;
