import React from "react";
import patientIcon from './images/patientIcon.png';
import Perscription from './startPerscription';

function Patient (props) {
  const { firstName, lastName, gender, email, cell } = props.patient;
  

  return (

    <div className="item" style = { {   
      borderRadius: "15px",
      border: '2px solid #000000',
      padding: '9pt',
      width: '100%',
    }}>
      <div style = {{display: "inline-block", minWidth: "400px"}}>
      <img className="ui avatar image" src={patientIcon} alt="user" style = { {   
        width: "45px",
        display: "inline-block",
        padding: "5pt",
        position: "absolute"
      }} />
      <div className="content" style = {{
        display: "inline-block",
        marginLeft: "50px"
      }}>
        <div className="header">{firstName} {lastName}</div>
        <div>{email}</div>
      </div>
      </div>

      <script src="https://unpkg.com/@popperjs/core@2"></script>

      <Perscription></Perscription>
      
      <button 
            className="delete"
            style = {{
                position:'absolute',
                borderRadius: '10px',
                color: 'Red',
                fontSize: '12pt',
                borderColor: 'Red',
                height: '45px',
                width: '80px',
                marginLeft: "26%",
                display: "inline-block",
             }} 
            > <b>Delete</b>
        </button>  
    </div>
  );
};

export default Patient;
