        <section class="hero image-as-background" style= {{backgroundImage: "url(https://live.staticflickr.com/65535/52209276873_40aaf3d4d9_k.jpg)"}}>
                <div class="hero-container ">
                    {/* <p class="animate fadeInLeft delay-400" >Mazgine cover hero style</p> */}
