                    }} >Pain Medication: Reimagined</h1>

                    <h2 class="text-left" style = {{marginTop: '0px', marginRight: 'auto', color: 'black', maxWidth: "750px", font: ''}}>
                        BalmCo is a Platform Dedicated to Revolutionizing the World of Pain Medication By Increasing Access to Compounded Topical Medications for All
                    </h2>
                    <button 
                        style={{ marginRight: 'auto', marginTop: '20px'}}
                        class="btn btn-outline-primary" href="#">Read More
                    </button>  
                </div>
            </section>
            </>
        );
        
    }
}
export default Hero;
