import React from "react";
// import patientIcon from './images/patientIcon.png';
import Perscription from '../doctor/startPerscription';
import {Modal, Button} from "react-bootstrap"

function PerscribedMed (props) {  

  return (
    <div style={{marginBottom:"5px", borderRadius: "5px", border: '.5px solid #000000', paddingTop: "10px", paddingLeft: "20px", paddingBottom: "10px", backgroundColor: "white",  minWidth: "800px"  }}>
      <div>
      <h3 style={{fontSize: "20px", display: "inline-block"}}>{props.formula}</h3>
      <h5 style={{fontSize: "10px", display: "inline-block"}}>
        Price: 
      </h5>
      </div>
      <h6>Perscribed on {props.date} by {props.doctor}</h6>
      <h6>Quantity Available: {props.amount}</h6>
    </div>
  );
};

export default PerscribedMed;
